from flask import Flaskfrom flask_migrate import Migratefrom flask_sqlalchemy import SQLAlchemyfrom App.Config import Configfrom extensions import db, migratedef create_app(config_class=Config):    app = Flask(__name__)    app.config.from_object(config_class)    app.config["SQLALCHEMY_DATABASE_URI"] = Config.Config.SQLALCHEMY_DATABASE_URI    db.init_app(app)    migrate.init_app(app, db)    from .api.resources import api_bp    app.register_blueprint(api_bp, url_prefix='/api')    return app