from flask import Flaskfrom flask_migrate import Migratefrom flask_sqlalchemy import SQLAlchemyfrom flask_jwt_extended import JWTManagerfrom App.Config import Configfrom extensions import db, migratedef create_app(config_class=Config):    app = Flask(__name__)    app.config.from_object(config_class)    app.config["SQLALCHEMY_DATABASE_URI"] = Config.Config.SQLALCHEMY_DATABASE_URI    app.config['JWT_SECRET_KEY'] = Config.Config.JWT_SECRET_KEY  # Use a securely generated key    app.config['SECRET_KEY'] = Config.Config.SECRET_KEY    db.init_app(app)    migrate.init_app(app, db)    jwt = JWTManager(app)    from .api.resourcesController import api_bp    app.register_blueprint(api_bp, url_prefix='/api')    return app