from flask import Blueprint, jsonify, requestfrom App.Models.User import Userfrom extensions import dbapi_bp = Blueprint('api', __name__)@api_bp.route('/users', methods=['GET'])def get_users():    users = User.query.all()    return jsonify([user.to_dict() for user in users])@api_bp.route('/user', methods=['POST'])def add_user():    data = request.get_json()    new_user = User(username=data['username'], email=data['email'])    db.session.add(new_user)    db.session.commit()    return jsonify({'message': 'User created successfully'}), 201